import React from "react";

function BlockChange(){

    const[back, setBack] = React.useState("red")
    const[bord, setBord] = React.useState("blue")
    const[num, setNum] =React.useState(0)

    let stylingBlock={
        backgroundColor: back,
        borderColor: bord,
        height: "100px",
        width: "100px",
        border: "2px"
    }

    let combine=()=>{
        setNum(num+1);

        if(num === 0){
            setBack("red")
            setBord("blue")
        }else if(num ===1){
            setBack("blue")
            setBord("green")
        }else{
            setBack("green")
            setBord("red")
        }


    }
    return(
        <div style={stylingBlock} onClick={combine}/>
    )

}
export default BlockChange;
