    const[num, setNum] =React.useState(0)
    const[increment, setIncrement]=React.useState(1);

    let stylingBlock={
        backgroundColor: back,
        borderColor: bord,
        borderWidth:"2px",
        borderStyle: "solid",
        height: "50px",
        width: "50px"
    }


    return(
        <div>
            <button onClick ={()=>setIncrement(increment+1)}>Increase by 1 : {increment}</button>
        </div>
    )


}
export default BlockChange;
