import React from "react";

function BlockChange(){

    const[back, setBack] = React.useState("blue")
    const[bord, setBord] = React.useState("white")
    const[num, setNum] =React.useState(0)
    const[increment, setIncrement]=React.useState(1);

    let stylingBlock={
        backgroundColor: back,
        borderColor: bord,
        borderWidth:"2px",
        borderStyle: "solid",
        height: "50px",
        width: "50px"
    }

    let combine=()=>{
        if(num === 0){
            setBack("blue")
            setBord("white")
            setNum(num+1)
        }else if(num ===1){
            setBack("purple")
            setBord("white")
            setNum(num+1)
        }else{
            setBack("green")
            setBord("white")
            setNum(0)
        }
    }
    return(
        <div>
            <button id="incrementalButton"
                onClick ={()=>setIncrement(increment+1)}>Increase by 1 : {increment}</button>
            <div style={stylingBlock} onClick={combine}/>
        </div>
    )


}
export default BlockChange;
