import React from "react";

function BlockChange(){
    let list =[
        ""
    ];

    //set states
    const[block, setBlock] = React.useState([0]);
    let [increment, setIncrement] = React.useState(0);
    const[back, setBack] = React.useState("blue");
    let [reset, setReset] = React.useState(false);



    let stylingBlock={
        backgroundColor: back,
        borderColor: "white",
        borderWidth:"2px",
        borderStyle: "solid",
        height: "50px",
        width: "50px",
        marginTop: "10px"
    }

    const addBlock = () => {
        if (setIncrement %2 != 0){
            let newBlock = block;
            stylingBlock.push(
                ""
            );
            setBlock(newBlock);
            console.log(block);
        }else{
            return setIncrement;
        }
    }





    return(
        <div>
           <button onClick ={()=>setIncrement(increment+1)}>Increase by 1 : {increment}</button>
            <button onClick ={()=>setIncrement(0)}>Click to Reset</button>
            <div style={addBlock()}/>
        </div>
    )


}
export default BlockChange;

